import json
import sys

j = sys.argv[1]
jsonObject = json.loads(j)
outputVarExists = False
if  "variables" in jsonObject:
 variables = jsonObject["variables"]
 for i, val in enumerate(variables):
  if "properties" in val:
   if "output" == val["properties"]["scope"]:
    outputVarExists=True
    break
print(str(outputVarExists).lower())
